from selenium import webdriverfrom selenium.webdriver.chrome.options import Optionsimport requestsdef save_page_as_pdf(url, filename):  """Saves a webpage as a PDF using Chrome."""  chrome_options = Options()  chrome_options.add_argument("--headless")  # Run Chrome in headless mode  chrome_options.add_argument("--disable-gpu")  # Disable GPU for headless mode  chrome_options.add_argument(f"--print-to-pdf={filename}") try:    response = requests.get('https://anaconda.org/conda-forge/python-pdfkit', timeout=90)  # Increase timeout to 90 seconds    response.raise_for_status()  # Raise an exception for bad status codes    # Process the response dataexcept requests.exceptions.RequestException as e:     print(f"Error: {e}")        driver = webdriver.Chrome(options=chrome_options)    driver.get(url)    driver.quit()if __name__ == "__main__":  url = "https://anaconda.org/conda-forge/python-pdfkit"    filename = "output.pdf"   save_page_as_pdf(url, filename)  #print(f"Page saved as {filename}")